 * This generated file contains a sample Java application project to get you started.
 * For more details on building Java & JVM projects, please refer to https://docs.gradle.org/8.11/userguide/building_java_projects.html in the Gradle documentation.
 */

plugins {
    // Apply the application plugin to add support for building a CLI application in Java.
    application
}

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
}

dependencies {
    
    // This dependency is used by the application.
    implementation(libs.guava)

    // cardano-client-lib
    implementation(libs.cardano.client.lib)

    // cardano-client-backend
    implementation(libs.cardano.client.backend)

    // cardano-client-backend-ogmios
    implementation(libs.cardano.client.backend.ogmios)

    // aiken-java-binding
    implementation(libs.aiken.java.binding)

    // slf4j
    implementation("org.slf4j:slf4j-simple:2.0.7")

    // Use JUnit Jupiter for testing.
    testImplementation(libs.junit.jupiter)
    testRuntimeOnly("org.junit.platform:junit-platform-launcher")

    // lombok
    testCompileOnly("org.projectlombok:lombok:1.18.24")
    testAnnotationProcessor("org.projectlombok:lombok:1.18.24")

    // yaci-cardano-test
    testImplementation(libs.yaci.cardano.test)
    
}

// Apply a specific Java toolchain to ease working on different environments.
java {
    toolchain {
        languageVersion = JavaLanguageVersion.of(17)
    }
}

application {
    // Define the main class for the application.
    mainClass = "org.example.App"
}

tasks.named<Test>("test") {
    // Use JUnit Platform for unit tests.
    useJUnitPlatform()
}
